package problem

import (
	"fmt"
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestP567(t *testing.T) {
	testcases := []struct {
		input1   string
		input2   string
		expected bool
	}{
		{
			input1:   "ab",
			input2:   "eidbaooo",
			expected: true,
		},
		{
			input1:   "ab",
			input2:   "eidboaoo",
			expected: false,
		},
		{
			input1:   "hello",
			input2:   "ooolleoooleh",
			expected: false,
		},
		{
			input1:   "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz",
			input2:   "",
			expected: true,
		},
		{
			input1:   "abc",
			input2:   "abbbbbbbbbbbbca",
			expected: true,
		},
	}

	for i, tc := range testcases {
		name := fmt.Sprintf("%d", i)
		t.Run(name, func(t *testing.T) {
			fmt.Printf("input1: %v\n", tc.input1)
			fmt.Printf("input2: %v\n", tc.input2)
			assert.Equal(t, tc.expected, checkInclusion(tc.input1, tc.input2))
		})
	}
}
